class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        n = len(circles)
        def touch(x,y,r):
            res = []
            dyup = abs(Y-y)
            dxup = 0 if 0<=x<=X else x-X

            if dyup**2 + dxup**2 <= r**2:
                res += [1]
            else:
                res += [0]

            dydown = y
            dxdown = 0 if 0<=x<=X else x-X
            if dydown**2 + dxdown**2 <= r**2:
                res += [1]
            else:
                res += [0]

            dyleft = 0 if y<=Y else y-Y
            dxleft = x
            if dxleft**2 + dyleft**2 <= r**2:
                res += [1]
            else:
                res += [0]

            dyright = 0 if y<=Y else y-Y
            dxright = abs(X-x)
            if dyright**2 + dxright**2 <= r**2:
                res += [1]
            else:
                res += [0]

            if (x-X)**2+(y-Y)**2 <= r**2:
                res += [1]
            else:
                res += [0]
            return res


        root = {i:i for i in range(-4, n)}

        def find(x):
            if root[x]!=x:
                root[x] = find(root[x])
            return root[x]

        def union(a, b):
            ra, rb = find(a), find(b)
            if ra != rb:
                root[ra] = rb 

        for i, [x,y,r] in enumerate(circles):
            for i2 in range(i):
                xi2, yi2, ri2 = circles[i2]
                if (xi2-x) ** 2 + (yi2-y)**2 <= (r + ri2)**2:
                    union(i, i2)
            t = touch(x,y,r)
            for j,v in enumerate(t, -4):
                if v:
                    union(i,j)


        for i,j in [(-4,-3), (-2, -1), (-4,-1), (-3,-2)]:
            if find(i) == find(j):
                return False
        return True
