class Solution {
public:
	typedef long long int ll;
	void f(int x, vector<vector<int>> &nb, vector<int> &gn, int k){
		if(gn[x] != -1){return;}
		gn[x] = k;
		for(auto y : nb[x]){
			f(y,nb,gn,k);
		}
	}
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
		int n = circles.size();
		vector<vector<ll>> A(n);
		for(int i=0;i<n;i++){
			A[i].resize(3);
			for(int j=0;j<3;j++){
				A[i][j] = circles[i][j];
			}
		}
        vector<vector<int>> nb(n);
		for(int i=0;i<n;i++){
			for(int j=0;j<n;j++){
				if((A[i][0]-A[j][0])*(A[i][0]-A[j][0]) + (A[i][1]-A[j][1])*(A[i][1]-A[j][1]) <= (A[i][2]+A[j][2])*(A[i][2]+A[j][2])){nb[i].push_back(j);}
			}
		}
		vector<int> gn(n,-1);
		int num_group = 0;
		for(int i=0;i<n;i++){
			if(gn[i] == -1){
				f(i,nb,gn,num_group);
				num_group++;
			}
		}
		for(int k=0;k<num_group;k++){
			bool tl = false, tr = false;
			for(int i=0;i<n;i++){
				if(gn[i] == k){
					if(A[i][2] >= A[i][0] || A[i][2] >= (Y-A[i][1])){tl = true;}
					if(A[i][2] >= A[i][1] || A[i][2] >= (X-A[i][0])){tr = true;}
				}
			}
			if(tl && tr){return false;}
		}
		return true;
    }
};
