class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        L1, L2 = set(), set()
        g = defaultdict(list)
        for c in circles:
            x1, y1, r1 = c
            c1 = tuple(c)
            if x1 - r1 <= 0 or y1 + r1 >= Y:
                L1.add(c1)
            if y1 - r1 <= 0 or x1 + r1 >= X:
                L2.add(c1)

            for x2, y2, r2 in circles:
                d = sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)
                if d <= r1 + r2:
                    c2 = (x2, y2, r2)
                    g[c1].append(c2)
                    g[c2].append(c1)

        q = deque(L1)
        while q:
            c = q.pop()
            if c in L2: return False

            for c2 in g[c]:
                if c2 not in L1:
                    L1.add(c2)
                    q.appendleft(c2)
        return True
