bool inter(int x1, int y1, int r1, int x2, int y2, int r2) {
    long long d = 1ll*(x1-x2)*(x1-x2) + 1ll*(y1-y2)*(y1-y2);
    return d <= 1ll*(r1+r2)*(r1+r2);
}

vector<vector<int>> G;
vector<int> was;
bool dfs(int x) {
    if (was[x]) return false;
    if (x==G.size()-1) return true;
    was[x]=1;
    for (int y : G[x]) {
        if (dfs(y)) return true;
    }
    return false;
}

class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& C) {
        int S = C.size();
        int T = C.size()+1;
        G = vector<vector<int>>(C.size()+2);
        was = vector<int>(C.size()+2, 0);
        for (int i = 0; i < C.size(); ++i) {
            int x1 = C[i][0];
            int y1 = C[i][1];
            int r1 = C[i][2];

            // intersects with start points
            if (inter(X,Y,0,x1,y1,r1)) return false;
            if (inter(0,0,0,x1,y1,r1)) return false;

            // ignore outside
            if (x1+r1<= 0 || x1-r1>=X) continue; 
            if (y1+r1<= 0 || y1-r1>=Y) continue;

            // touches S
            if (x1-r1<=0 || y1+r1>=Y) {
                G[i].push_back(S);
                G[S].push_back(i);
            }
            if (x1+r1>=X || y1-r1<=0) {
                G[i].push_back(T);
                G[T].push_back(i);
            }

            for (int j = i+1; j < C.size(); ++j) {
                int x2 = C[j][0];
                int y2 = C[j][1];
                int r2 = C[j][2];
                if (inter(x1,y1,r1,x2,y2,r2)) {
                    G[i].push_back(j);
                    G[j].push_back(i);
                }
            }
        }
        return !dfs(S);
    }
};
