using namespace std;

class Solution {
public:
    vector<vector<int>> a;
    vector <int> g[N];
    bool was[N];

    int c0, c1;
    void dfs(int v, int X, int Y) {
        was[v] = 1;
        if (a[v][0] <= a[v][2] || (Y - a[v][1]) <= a[v][2]) c0++;
        if (a[v][1] <= a[v][2] || (X - a[v][0]) <= a[v][2]) c1++;

        for (auto to : g[v]) {
            if (!was[to]) dfs(to, X, Y);
        }
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& A) {
        a = A;

        rep(i, 0, sz(a) - 1) {
            ll x = a[i][0], y = a[i][1], r = a[i][2];
            rep(j, i + 1, sz(a) - 1) {
                ll x1 = a[j][0], y1 = a[j][1], r1 = a[j][2];

                if (sqr(x - x1) + sqr(y - y1) <= sqr(r1 + r)) {
                    g[i].pb(j);
                    g[j].pb(i);
                }
            }
        }
        rep(i, 0, sz(a) - 1) {
            if (!was[i]) {
                c0 = c1 = 0;
                dfs(i, X, Y);
                if (c0 && c1) return 0;
            }
        }
        return 1;
    }
};
