#define ll long long
class Solution {
public:

    vector<bool> bfs(int s,vector<ll> ed[],int n)
    {
      vector<bool> bv(n,0);
      queue<int> q;
      q.push(s);
      bv[s] = 1;

      while(!q.empty())
      {
        int f = q.front();

        q.pop();

        for(auto&a:ed[f])
          if(!bv[a])
          {
            bv[a] = 1;
            q.push(a);
          }
      }
      return bv;
    }
    bool canReachCorner(int x, int y, vector<vector<int>>& cr) {

        ll n = cr.size();
        vector<ll> ed[n+4];

          for(int i = 0 ; i < cr.size() ; ++i)
          {

            if(cr[i][0]-cr[i][2]<=0)
            {
              ed[i].push_back(n);
              ed[n].push_back(i);
            }

            if(cr[i][1]-cr[i][2]<=0)
            {
              ed[i].push_back(n+1);
              ed[n+1].push_back(i);
            }

            if(cr[i][0]+cr[i][2]>=x)
            {
              ed[i].push_back(n+2);
              ed[n+2].push_back(i);
            }

            if(cr[i][1]+cr[i][2]>=y)
            {
              ed[i].push_back(n+3);
              ed[n+3].push_back(i);
            }
          }

        for(int i = 0 ; i < n ; ++i)
            for(int j = i+1 ; j < n ; ++j)
            {
              if( 1ll*(cr[i][0]-cr[j][0])*1ll*(cr[i][0]-cr[j][0]) + 1ll*(cr[i][1]-cr[j][1])*1ll*(cr[i][1]-cr[j][1]) <= 1ll*(cr[i][2]+cr[j][2])*1ll*(cr[i][2]+cr[j][2]))
              {
                ed[i].push_back(j);
                ed[j].push_back(i);
              }
            }

          vector<bool> L = bfs(n,ed,n+4);
          vector<bool> B = bfs(n+1,ed,n+4);
          vector<bool> R = bfs(n+2,ed,n+4);
          vector<bool> T = bfs(n+3,ed,n+4);


          if(L[n+1]||B[n+3]||B[n]||L[n+2]||T[n+2]||T[n+1]||R[n+3]||R[n])
            return false;

        return true;
    }
};
