class Solution {
public:
    int bfs(vector<vector<int>> &adj, int start, int target1, int target2, int nodes) {
        queue<int> q;
        vector<int> visited(nodes + 4, 0);
        q.push(start);
        visited[start] = 1;

        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v : adj[u]) {
                if (!visited[v]) {
                    q.push(v);
                    visited[v] = 1;
                }
            }
        }
        return visited[target1] || visited[target2];
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& Circles) {
        vector<pair<pair<int, int>, int>> circles(Circles.size());
         vector<vector<int>> adj(Circles.size() + 4);
        int num_circles=Circles.size();

        for (int i = 0; i < num_circles; ++i) {
            circles[i].first.first=Circles[i][0];
            circles[i].first.second=Circles[i][1];
            circles[i].second=Circles[i][2];
        }
        for (int i = 0; i < num_circles; ++i) {

            if (circles[i].first.first <= circles[i].second) {
                adj[num_circles].push_back(i);
                adj[i].push_back(num_circles);
            }
            if (X - circles[i].first.first <= circles[i].second) {
                adj[num_circles + 2].push_back(i);
                adj[i].push_back(num_circles + 2);
            }
            if (circles[i].first.second <= circles[i].second) {
                adj[num_circles + 1].push_back(i);
                adj[i].push_back(num_circles + 1);
            }
            if (Y - circles[i].first.second <= circles[i].second) {
                adj[num_circles + 3].push_back(i);
                adj[i].push_back(num_circles + 3);
            }
            for (int j = i + 1; j < num_circles; ++j) {
                double distance = sqrt(pow(circles[i].first.first - circles[j].first.first, 2) +
                                       pow(circles[i].first.second - circles[j].first.second, 2));
                int radius_sum = circles[i].second + circles[j].second;
                if (radius_sum >= distance) {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }
        int blocked = 0;
        blocked = max(blocked, bfs(adj, num_circles, num_circles + 1, num_circles + 2, num_circles + 4));
        blocked = max(blocked, bfs(adj, num_circles + 3, num_circles + 2, num_circles + 1, num_circles + 4)); 
        if (blocked){return false;}
        return true;
    }
};
