class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        def dist(x,y,xx,yy):
            return sqrt((x-xx)**2+(y-yy)**2)
        g=defaultdict(set)
        lt=set()
        rb=set()
        for i,(x,y,r) in enumerate(circles):
            if x-r<=0 or y+r>=Y:
                lt.add(i)
            if x+r>=X or y-r<=0:
                rb.add(i)
            for j,(x2,y2,r2) in enumerate(circles):
                if dist(x,y,x2,y2)<=r+r2:
                    g[i].add(j)
        q=lt
        seen=set(q)
        while q:
           # print(q)
            q2=set()
            for x in q:
                if x in rb:
                    return False
                for y in g[x]:
                    if y not in seen:
                        q2.add(y)
                        seen.add(y)
            q=q2
        return True
