typedef long long LL;

class Solution {
    bool check(LL x, LL y, LL z) {
        return x * x + y * y <= z * z;
    }

public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<vector<bool>> e(n + 2, vector<bool>(n + 2));
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++)
                if (check(circles[i][0] - circles[j][0], circles[i][1] - circles[j][1], circles[i][2] + circles[j][2]))
                    e[i][j] = e[j][i] = true;
            if (circles[i][0] <= X && circles[i][1] <= circles[i][2]
                    || circles[i][1] <= Y && abs(X - circles[i][0]) <= circles[i][2])
                e[i][n] = e[n][i] = true;
            if (circles[i][1] <= Y && circles[i][0] <= circles[i][2]
                    || circles[i][0] <= X && abs(Y - circles[i][1]) <= circles[i][2])
                e[i][n + 1] = e[n + 1][i] = true;
        }

        vector<bool> b(n + 2);
        b[n] = true;
        queue<int> q;
        q.push(n);
        while (!q.empty()) {
            int i = q.front();
            q.pop();
            for (int j = 0; j <= n + 1; j++)
                if (e[i][j] && !b[j]) {
                    b[j] = true;
                    q.push(j);
                }
        }
        return !b[n + 1];
    }
};
