class Solution {
public:

    int bfs(vector<vector<int>> &graph, int start, int target1, int target2, int nodes) {
        queue<int> q;
        vector<int> visited(nodes + 4, 0);
        q.push(start);
        visited[start] = 1;

        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v : graph[u]) {
                if (!visited[v]) {
                    q.push(v);
                    visited[v] = 1;
                }
            }
        }
        return visited[target1] || visited[target2];
    }

    bool canReachCorner(int width, int height, vector<vector<int>>& circlesInput) {
        vector<pair<pair<int, int>, int>> circles(circlesInput.size());
        vector<vector<int>> graph(circlesInput.size() + 4);
        int numCircles = circlesInput.size();

        for (int i = 0; i < numCircles; ++i) {
            circles[i].first.first = circlesInput[i][0];
            circles[i].first.second = circlesInput[i][1];
            circles[i].second = circlesInput[i][2];
        }

        for (int i = 0; i < numCircles; ++i) {
            if (circles[i].first.first <= circles[i].second) {
                graph[numCircles].push_back(i);
                graph[i].push_back(numCircles);
            }
            if (width - circles[i].first.first <= circles[i].second) {
                graph[numCircles + 2].push_back(i);
                graph[i].push_back(numCircles + 2);
            }
            if (circles[i].first.second <= circles[i].second) {
                graph[numCircles + 1].push_back(i);
                graph[i].push_back(numCircles + 1);
            }
            if (height - circles[i].first.second <= circles[i].second) {
                graph[numCircles + 3].push_back(i);
                graph[i].push_back(numCircles + 3);
            }
            for (int j = i + 1; j < numCircles; ++j) {
                double dist = sqrt(pow(circles[i].first.first - circles[j].first.first, 2) +
                                   pow(circles[i].first.second - circles[j].first.second, 2));
                int radiusSum = circles[i].second + circles[j].second;
                if (radiusSum >= dist) {
                    graph[i].push_back(j);
                    graph[j].push_back(i);
                }
            }
        }

        int isBlocked = 0;
        isBlocked = max(isBlocked, bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4)); // Check for bottom-left corner
        isBlocked = max(isBlocked, bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4)); // Check for top-right corner

        return !isBlocked;
    }
};
