typedef long long ll;
class Solution {
public:
    ll dist_sq(pair<ll, ll> a, pair<ll, ll> b){
        return pow(a.first - b.first, 2) + pow(a.second - b.second, 2);
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        int br = n;
        int tl = n + 1;
        vector<vector<int>> c(n + 2);
        for(int i = 0; i < circles.size(); i++){
            ll x = circles[i][0];
            ll y = circles[i][1];
            ll r = circles[i][2];
            //if i don't intersect the square, disregard
            if(x < 0 || y < 0 || x > X || y > Y) {
                if(x < -r || x > X + r || y < -r || y > Y + r){
                    continue;
                }
            }
            //other circles
            for(int j = 0; j < n; j++){
                ll xo = circles[j][0];
                ll yo = circles[j][1];
                ll ro = circles[j][2];
                if(dist_sq({x, y}, {xo, yo}) <= pow(r + ro, 2)) {
                    c[i].push_back(j);
                }
            }
            //sides
            bool t_br = false, t_tl = false;
            if(x <= r){
                t_tl = true;
            }
            if(y <= r){
                t_br = true;
            }
            if(abs(x - X) <= r) {
                t_br = true;
            }
            if(abs(y - Y) <= r){
                t_tl = true;
            }
            if(dist_sq({x, y}, {X, Y}) <= r * r) {
                t_br = true;
                t_tl = true;
            }
            if(t_br) {
                c[br].push_back(i);
            }
            if(t_tl){
                c[i].push_back(tl);
            }
        }
        //bfs
        vector<bool> v(n + 2, false);
        queue<int> q;
        q.push(br);
        v[br] = true;
        while(q.size() != 0){
            int cur = q.front();
            q.pop();
            for(int i = 0; i < c[cur].size(); i++){
                int next = c[cur][i];
                if(!v[next]) {
                    v[next] = true;
                    q.push(next);
                }
            }
        }
        return !v[tl];
    }
};
