class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:

        def touches(c1, c2):
            x1, y1, r1 =  circles[c1]
            x2, y2, r2 =  circles[c2]

            return (r1+r2)**2 >= (y2-y1)**2 + (x2-x1)**2

        g = defaultdict(list)
        n = len(circles)

        e1 = list()
        e2 = set()

        for c1 in range(n):
            x, y, r = circles[c1]
            if x <= r or Y-y <= r:
                e1.append(c1)
            if X-x <= r or y <= r:
                e2.add(c1)
            for c2 in range(c1+1, n):
                if touches(c1, c2):
                    g[c1].append(c2)
                    g[c2].append(c1)

        visited = set(e1)
        for c in e1:
            if c in e2:
                return False
            for to in g[c]:
                if to not in visited:
                    visited.add(to)
                    e1.append(to)

        return True
