class Solution {
public:
   bool trav(vector<vector<int>>& gr, int st, int t1, int t2, int nd) {
        queue<int> q;
        vector<int> vis(nd, 0);
        q.push(st);
        vis[st] = 1;

        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v : gr[u]) {
                if (!vis[v]) {
                    q.push(v);
                    vis[v] = 1;
                }
            }
        }
        return vis[t1] || vis[t2];
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& cr2) {
        int nc = cr2.size();
        vector<pair<pair<int, int>, int>> cr(nc);
        vector<vector<int>> gr(nc + 4);

        int i = 0;
        while (i < nc) {
            cr[i] = {{cr2[i][0], cr2[i][1]}, cr2[i][2]};
            ++i;
        }

        i = 0;
        while (i < nc) {
            if (cr[i].first.first <= cr[i].second) {
                gr[nc].push_back(i);
                gr[i].push_back(nc);
            }
            if (X - cr[i].first.first <= cr[i].second) {
                gr[nc + 2].push_back(i);
                gr[i].push_back(nc + 2);
            }
            if (cr[i].first.second <= cr[i].second) {
                gr[nc + 1].push_back(i);
                gr[i].push_back(nc + 1);
            }
            if (Y - cr[i].first.second <= cr[i].second) {
                gr[nc + 3].push_back(i);
                gr[i].push_back(nc + 3);
            }
            int j = i + 1;
            while (j < nc) {
                double ds = sqrt(pow(cr[i].first.first - cr[j].first.first, 2) +
                                 pow(cr[i].first.second - cr[j].first.second, 2));
                int rs = cr[i].second + cr[j].second;
                if (rs >= ds) {
                    gr[i].push_back(j);
                    gr[j].push_back(i);
                }
                ++j;
            }
            ++i;
        }

        if (trav(gr, nc, nc + 1, nc + 2, nc + 4) || trav(gr, nc + 3, nc + 2, nc + 1, nc + 4)) {
            return false;
        }
        return true;

    }
};
