class Solution:
    def canReachCorner(self, width, height, circles):
        neighbors = [[] for _ in range(len(circles) + 4)]

        for idx, circle in enumerate(circles):
            index = idx + 4
            cx, cy, radius = circle
            if cx - radius <= 0:
                neighbors[0].append(index)
                neighbors[index].append(0)
            if cx + radius >= width:
                neighbors[1].append(index)
                neighbors[index].append(1)
            if cy - radius <= 0:
                neighbors[3].append(index)
                neighbors[index].append(3)
            if cy + radius >= height:
                neighbors[2].append(index)
                neighbors[index].append(2)

        for i in range(len(circles)):
            for j in range(i + 1, len(circles)):
                a_index, b_index = i + 4, j + 4
                cx1, cy1, r1 = circles[i]
                cx2, cy2, r2 = circles[j]
                distance = (cx1 - cx2) ** 2 + (cy1 - cy2) ** 2
                combined_radius = (r1 + r2) ** 2
                if combined_radius >= distance:
                    neighbors[a_index].append(b_index)
                    neighbors[b_index].append(a_index)

        visited = [False] * (len(circles) + 4)

        def dfs(start, target):
            stack = [start]
            while stack:
                current = stack.pop()
                if current == target:
                    return True
                if not visited[current]:
                    visited[current] = True
                    for neighbor in neighbors[current]:
                        if not visited[neighbor]:
                            stack.append(neighbor)
            return False

        for start, end in [(0, 1), (0, 3), (2, 3), (2, 1)]:
            visited = [False] * (len(circles) + 4)
            if dfs(start, end):
                return False
        return True
