class Solution {
    public int bfs(List<List<Integer>> graph, int start, int target1, int target2, int nodes) {
        Queue<Integer> queue = new LinkedList<>();
        boolean[] visited = new boolean[nodes + 4];
        queue.add(start);
        visited[start] = true;

        while (!queue.isEmpty()) {
            int u = queue.poll();
            for (int v : graph.get(u)) {
                if (!visited[v]) {
                    queue.add(v);
                    visited[v] = true;
                }
            }
        }

        return visited[target1] || visited[target2] ? 1 : 0;
    }

    public boolean canReachCorner(int X, int Y, int[][] circles2) {
        int numCircles = circles2.length;
        List<int[]> circles = new ArrayList<>(numCircles);
        List<List<Integer>> graph = new ArrayList<>(numCircles + 4);

        for (int i = 0; i < numCircles + 4; i++) {
            graph.add(new ArrayList<>());
        }

        for (int i = 0; i < numCircles; i++) {
            circles.add(new int[]{circles2[i][0], circles2[i][1], circles2[i][2]});
        }

        for (int i = 0; i < numCircles; i++) {
            int[] circle = circles.get(i);
            if (circle[0] <= circle[2]) {
                graph.get(numCircles).add(i);
                graph.get(i).add(numCircles);
            }
            if (X - circle[0] <= circle[2]) {
                graph.get(numCircles + 2).add(i);
                graph.get(i).add(numCircles + 2);
            }
            if (circle[1] <= circle[2]) {
                graph.get(numCircles + 1).add(i);
                graph.get(i).add(numCircles + 1);
            }
            if (Y - circle[1] <= circle[2]) {
                graph.get(numCircles + 3).add(i);
                graph.get(i).add(numCircles + 3);
            }
            for (int j = i + 1; j < numCircles; j++) {
                int[] otherCircle = circles.get(j);
                double distance = Math.sqrt(Math.pow(circle[0] - otherCircle[0], 2) +
                                            Math.pow(circle[1] - otherCircle[1], 2));
                int radiusSum = circle[2] + otherCircle[2];
                if (radiusSum >= distance) {
                    graph.get(i).add(j);
                    graph.get(j).add(i);
                }
            }
        }

        int isBlocked = 0;
        isBlocked = Math.max(isBlocked, bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4));
        isBlocked = Math.max(isBlocked, bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4));

        return isBlocked == 0;
    }
}
