class Solution {
public:
    // Function to perform Breadth-First Search (BFS)
    int bfs(const vector<vector<int>>& g, int s, int t1, int t2, int n) {
        queue<int> q;
        vector<int> vis(n + 4, 0);
        q.push(s);
        vis[s] = 1;

        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v : g[u]) {
                if (!vis[v]) {
                    q.push(v);
                    vis[v] = 1;
                }
            }
        }
        return vis[t1] || vis[t2];
    }

    // Function to determine if one can reach a corner
    bool canReachCorner(int X, int Y, vector<vector<int>>& c2) {
        int n = c2.size();
        vector<pair<pair<int, int>, int>> c(n);
        vector<vector<int>> g(n + 4);

        // Convert c2 to the circles vector
        for (int i = 0; i < n; ++i) {
            c[i] = {{c2[i][0], c2[i][1]}, c2[i][2]};
        }

        // Build the graph
        for (int i = 0; i < n; ++i) {
            // Check if circles touch the borders
            if (c[i].first.first <= c[i].second) {
                g[n].push_back(i);
                g[i].push_back(n);
            }
            if (X - c[i].first.first <= c[i].second) {
                g[n + 2].push_back(i);
                g[i].push_back(n + 2);
            }
            if (c[i].first.second <= c[i].second) {
                g[n + 1].push_back(i);
                g[i].push_back(n + 1);
            }
            if (Y - c[i].first.second <= c[i].second) {
                g[n + 3].push_back(i);
                g[i].push_back(n + 3);
            }
            // Check if circles overlap
            for (int j = i + 1; j < n; ++j) {
                double dist = sqrt(pow(c[i].first.first - c[j].first.first, 2) +
                                   pow(c[i].first.second - c[j].first.second, 2));
                int r_sum = c[i].second + c[j].second;
                if (r_sum >= dist) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        // Check if there is a block from start to any corner
        bool is_blocked = false;
        is_blocked = is_blocked || bfs(g, n, n + 1, n + 2, n + 4);
        is_blocked = is_blocked || bfs(g, n + 3, n + 2, n + 1, n + 4);

        return !is_blocked;
    }
};
