#include <vector>
#include <cmath>
#include <algorithm>

using namespace std;

class Solution {
public:
    bool dfs(vector<vector<int>> &g, vector<int> &vis, int u, int t1, int t2) {
        if (u == t1 || u == t2) return true;
        vis[u] = 1;
        for (int v : g[u]) {
            if (!vis[v] && dfs(g, vis, v, t1, t2)) return true;
        }
        return false;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& c2) {
        ios_base::sync_with_stdio(false);
    cin.tie(NULL);
        vector<pair<pair<int, int>, int>> c(c2.size());
        vector<vector<int>> g(c2.size() + 4);
        int nc = c2.size();

        int i = 0;
        while (i < nc) {
            c[i].first.first = c2[i][0];
            c[i].first.second = c2[i][1];
            c[i].second = c2[i][2];
            ++i;
        }

        i = 0;
        while (i < nc) {
            if (c[i].first.first <= c[i].second) {
                g[nc].push_back(i);
                g[i].push_back(nc);
            }
            if (X - c[i].first.first <= c[i].second) {
                g[nc + 2].push_back(i);
                g[i].push_back(nc + 2);
            }
            if (c[i].first.second <= c[i].second) {
                g[nc + 1].push_back(i);
                g[i].push_back(nc + 1);
            }
            if (Y - c[i].first.second <= c[i].second) {
                g[nc + 3].push_back(i);
                g[i].push_back(nc + 3);
            }
            int j = i + 1;
            while (j < nc) {
                double d = sqrt(pow(c[i].first.first - c[j].first.first, 2) + pow(c[i].first.second - c[j].first.second, 2));
                int rs = c[i].second + c[j].second;
                if (rs >= d) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
                ++j;
            }
            ++i;
        }

        vector<int> vis(nc + 4, 0);
        if (dfs(g, vis, nc, nc + 1, nc + 2) || dfs(g, vis, nc + 3, nc + 2, nc + 1)) return false;
        return true;
    }
};
