class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {
        int n = c.size();
        vector<int> p(n+4);
        iota(p.begin(),p.end(),0);
        auto f = [&](auto self,int u)->int{
            if(p[u]!=u) p[u]=self(self,p[u]);
            return p[u];
        };
        auto join = [&](int x,int y){
            x=f(f,x),y=f(f,y);
            if(x!=y) p[x]=y;
        };
        using ll = long long;
        for(int i=0;i<n;i++){
            for(int j=0;j<i;j++){
                ll dx = c[j][0]-c[i][0],dy=c[j][1]-c[i][1],tr = c[j][2]+c[i][2];
                if(dx*dx+dy*dy<=tr*tr) join(i,j);
            }
        }
        for(int i=0;i<n;i++){
            int x = c[i][0],y=c[i][1],r=c[i][2];
            if(x-r<=0&&x+r>=0) join(i,n);
            if(x-r<=X&&x+r>=X) join(i,n+2);
            if(y-r<=0&&y+r>=0) join(i,n+1);
            if(y-r<=Y&&y+r>=Y) join(i,n+3);
        }
        int a = f(f,n),b=f(f,n+1),d=f(f,n+2),e=f(f,n+3);
        if(a==b || d==e || a==d || b==e) return false;
        return true;
    }
};
