class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& c) 
    {
        int n=c.size();
        vector<vector<long long>> circles;
        for(int i=0;i<n;++i)
            circles.push_back({c[i][0],c[i][1],c[i][2]});
        vector<vector<long long>> ck(n,vector<long long>(4));
        vector<int> p(n);
        for(int i=0;i<n;++i)
        {
            ck[i][0]=circles[i][0]-circles[i][2];
            ck[i][1]=circles[i][0]+circles[i][2];
            ck[i][2]=circles[i][1]-circles[i][2];
            ck[i][3]=circles[i][1]+circles[i][2];
            p[i]=i;
        }
        for(int i=0;i<n;++i)
        {
            for(int j=0;j<n;++j)
            {
                if(i==j)
                    continue;
                long long r=circles[i][2]+circles[j][2];
                if(0LL+(circles[i][0]-circles[j][0])*(circles[i][0]-circles[j][0])+(circles[i][1]-circles[j][1])*(circles[i][1]-circles[j][1])<=r*r)
                {
                    if(fp(p,i)<fp(p,j))
                    {
                        ck[fp(p,i)][0]=min(ck[fp(p,i)][0],ck[fp(p,j)][0]);
                        ck[fp(p,i)][1]=max(ck[fp(p,i)][1],ck[fp(p,j)][1]);
                        ck[fp(p,i)][2]=min(ck[fp(p,i)][2],ck[fp(p,j)][2]);
                        ck[fp(p,i)][3]=max(ck[fp(p,i)][3],ck[fp(p,j)][3]);
                        p[fp(p,j)]=fp(p,i);
                        if((ck[fp(p,i)][0]<=0 && ck[fp(p,i)][1]>=X) || (ck[fp(p,i)][2]<=0 && ck[fp(p,i)][3]>=Y))
                            return false;
                        if((ck[fp(p,i)][0]<=0 && ck[fp(p,i)][2]<=0) || (ck[fp(p,i)][1]>=X && ck[fp(p,i)][3]>=Y))
                            return false;
                    }
                    else
                    {
                        ck[fp(p,j)][0]=min(ck[fp(p,i)][0],ck[fp(p,j)][0]);
                        ck[fp(p,j)][1]=max(ck[fp(p,i)][1],ck[fp(p,j)][1]);
                        ck[fp(p,j)][2]=min(ck[fp(p,i)][2],ck[fp(p,j)][2]);
                        ck[fp(p,j)][3]=max(ck[fp(p,i)][3],ck[fp(p,j)][3]);
                        p[fp(p,i)]=fp(p,j);
                        if((ck[fp(p,j)][0]<=0 && ck[fp(p,j)][1]>=X) || (ck[fp(p,j)][2]<=0 && ck[fp(p,j)][3]>=Y))
                            return false;
                        if((ck[fp(p,j)][0]<=0 && ck[fp(p,j)][2]<=0) || (ck[fp(p,j)][1]>=X && ck[fp(p,j)][3]>=Y))
                            return false;
                    }
                }
            }
        }
        for(int i=0;i<n;++i)
        {
            if((ck[fp(p,i)][0]<=0 && ck[fp(p,i)][1]>=X) || (ck[fp(p,i)][2]<=0 && ck[fp(p,i)][3]>=Y))
                return false;
            if((ck[fp(p,i)][0]<=0 && ck[fp(p,i)][2]<=0) || (ck[fp(p,i)][1]>=X && ck[fp(p,i)][3]>=Y))
                return false;
        }
        return true;
    }
private:
    int fp(vector<int> &p, int n)
    {
        if(p[n]==n)
            return n;
        return p[n]=fp(p,p[n]);
    }
};
