typedef long long ll;
struct DSU
{

    vector<int> par, size;

    DSU(int n)
    {

        par.resize(n + 1);
        size.resize(n + 1, 1);

        for (int j = 1; j <= n; j++)
        {
            par[j] = j;
        }
    }

    int Leader(int x)
    {
        if (par[x] == x)
            return x;
        return (par[x] = Leader(par[x]));
    }

    void merge(int x, int y)
    {
        x = Leader(x);
        y = Leader(y);

        if (x == y)
            return;
        if (size[x] > size[y])
            swap(x, y);

        size[y] += size[x];
        par[x] = y;
    }
};

class Solution {
    bool intersect(ll x,ll y,ll x0,ll y0,ll r){
        ll d = (x - x0)*1ll*(x - x0);
        d += (y - y0) * 1ll * (y - y0);

        return d <= r*1ll*r;
    }
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();

        DSU dsu(n);

        for(int i = 0;i<n;i++){
            for(int j = i + 1;j<n;j++){
                ll x1 = circles[i][0],y1 = circles[i][1],r1 = circles[i][2];
                ll x2 = circles[j][0],y2 = circles[j][1],r2 = circles[j][2];

                if(intersect(x1,y1,x2,y2,r1+r2))dsu.merge(i,j);
            }
        }


        for(int i = 0;i<n;i++){
            for(int j = 0 ; j < n; j++){

                if(dsu.Leader(i) == dsu.Leader(j)){
                ll x1 = circles[i][0],y1 = circles[i][1],r1 = circles[i][2];
                ll x2 = circles[j][0],y2 = circles[j][1],r2 = circles[j][2];

                    if(intersect(x1,y1,x1,0,r1) && intersect(x2,y2,x2,Y,r2)){
                        return 0;
                    }

                    if(intersect(x1,y1,x1,Y,r1) && intersect(x2,y2,X,y2,r2)){
                        return 0;
                    }

                    if(intersect(x1,y1,0,y1,r1) && intersect(x2,y2,x2,0,r2)){
                        return 0;
                    }

                    if(intersect(x1,y1,0,y1,r1) && intersect(x2,y2,X,y2,r2)){
                       return 0; 
                    }
                }
            }
        }

        return 1;
    }
};
