class Solution {
public:    
    bool canReachCorner(int X, int Y, vector<vector<int>>& cir) {
        vector<int> left , right , top ,bottom;
        auto dist = [&](vector<int> &c1,vector<int> &c2){
            long long int x_part = 1ll*(c1[0] - c2[0])*(c1[0]-c2[0]);
            long long int y_part = 1ll*(c1[1] - c2[1])*(c1[1]-c2[1]);
            return x_part+y_part<=(1ll*(c1[2]+c2[2])*(c1[2]+c2[2]));
        };
        for(int i=0;i<cir.size();i++){
            auto c = cir[i];
            if(c[1]<=Y && c[0]<=c[2])left.push_back(i);
            if(c[0]<=X && c[1]<=c[2])bottom.push_back(i);
            if(c[0]<=X && abs(c[1]-Y)<=c[2])top.push_back(i);
            if(c[1]<=Y && abs(c[0]-X)<=c[2])right.push_back(i);
        }
        int n = cir.size();
        vector<int> vis(n,false);

        bool ok=true;

        function<void(int)> dfs=[&](int node){
            if(vis[node])return;
            vis[node]=true;
            for(int i=0;i<n;i++){
                if(i==node)continue;
                if(dist(cir[node],cir[i]))dfs(i);
            }
        };

        // dfs left

        for(int i=0;i<left.size();i++){
            dfs(left[i]);
        }

        for(int i=0;i<bottom.size();i++){
            if(vis[bottom[i]]==1){
                return false;
            }
        }

        for(int i=0;i<right.size();i++){
            if(vis[right[i]]==1){
                return false;
            }
        }

        // dfs top

        vis.clear();
        vis.resize(n,false);

        for(int i=0;i<top.size();i++){
            dfs(top[i]);
        }

        for(int i=0;i<bottom.size();i++){
            if(vis[bottom[i]]==1){
                return false;
            }
        }

        for(int i=0;i<right.size();i++){
            if(vis[right[i]]==1){
                return false;
            }
        }

        return true;
    }
};
