class Solution {
public:
    bool canReachCorner(int x, int y, vector<vector<int>>& cs) {
        int n = cs.size();
        vector<int> touches(n);
        for (int i=0;i<n;i++) {
            for (int j=0;j<i;j++) {
                if (j==i) continue;
                if (circlesTouch(cs[i],cs[j])) touches[i] |= touches[j];
            }
            touches[i] |= touchEdge(cs[i],x,y);
            if (touches[i]==3) return false;
        }
        return true;
    }
    int touchEdge(vector<int>& c, int x, int y) {
        int cx = c[0], cy = c[1], r = c[2];
        int ret = 0;
        if (abs(cx-0)<=r && !(cy+r<0 || cy-r>y)) ret |= 2;
        if (abs(cy-y)<=r && !(cx+r<0 || cx-r>x)) ret |= 2;

        if (abs(cx-x)<=r && !(cy+r<0 || cy-r>y)) ret |= 1;
        if (abs(cy-0)<=r && !(cx+r<0 || cx-r>x)) ret |= 1;
        return ret;
    }
    bool circlesTouch(vector<int>& c1, vector<int>& c2) {
        long long dist = (c2[0]-c1[0])*1ll*(c2[0]-c1[0])+(c2[1]-c1[1])*1ll*(c2[1]-c1[1]);
        return dist <= (c1[2]+c2[2])*1ll*(c1[2]+c2[2]);
    }
};
