#include "bits/stdc++.h"
using namespace std;

#define endl '\n'
#define rep(i, a, b) for (int i = a; i < (b); i++)
#define all(x) (x).begin(), (x).end()
#define rall(x) (x).rbegin(), (x).rend()
#define sz(x) (int)(x).size()
#define sq(x) (x)*(x)
template<class T, class U> inline void chmin(T& a, U b) { if (a > b) a = b; }
template<class T, class U> inline void chmax(T& a, U b) { if (a < b) a = b; }
mt19937 rng((int) chrono::steady_clock::now().time_since_epoch().count());
typedef long long ll;
typedef long double ld;
typedef __int128 i128;

struct dsu {
    vector<int> p, rnk;
    vector<int> mnX, mnY, mxX, mxY;
    dsu(int n) : p(n), rnk(n, 1) {
        iota(all(p), 0);
        mnX.resize(n);
        mnY.resize(n);
        mxX.resize(n);
        mxY.resize(n);
    }
    int find(int x) { return p[x] == x ? x : p[x] = find(p[x]); }
    bool join(int a, int b) {
        a = find(a); b = find(b);
        if (a == b) return false;
        if (rnk[a] < rnk[b]) swap(a, b);
        p[b] = a;
        chmin(mnX[a], mnX[b]);
        chmin(mnY[a], mnY[b]);

        chmax(mxX[a], mxX[b]);
        chmax(mxY[a], mxY[b]);
        rnk[a] += rnk[b];
        return true;
    }
    bool same(int a, int b) { return find(a) == find(b); }
};

bool intersect(ll x1, ll y1, ll r1, ll x2, ll y2, ll r2) {
    ll d = sq(x1 - x2) + sq(y1 - y2);
    return d <= sq(r1 + r2);
}

class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {
        int n = sz(c);
        dsu g(n);
        rep(i, 0, n) {
            g.mnX[i] = c[i][0] - c[i][2];
            g.mxX[i] = c[i][0] + c[i][2];

            g.mnY[i] = c[i][1] - c[i][2];
            g.mxY[i] = c[i][1] + c[i][2];
        }
        rep(i, 0, n) rep(j, i + 1, n) {
            if (intersect(c[i][0], c[i][1], c[i][2], c[j][0], c[j][1], c[j][2])) {
                g.join(i, j);
            }
        }
        rep(i, 0, n) {
            if ((g.mnX[i] <= 0 && g.mxX[i] >= X) ||
                (g.mnY[i] <= 0 && g.mxY[i] >= Y) ||
                (g.mnX[i] <= 0 && g.mnY[i] <= 0) ||
                (g.mxX[i] >= X && g.mxY[i] >= Y)) {
                return false;
            }
        }
        return true;
    }
};
