class Solution {
public:
      #define  ll long long
    bool ans = 1;

    void dsu(int i, int X, int Y, vector<bool> &vis, vector<vector<int>> &dp,vector<vector<int>>& circles,vector<int> &touch)
    {

         vis[i] = 1;
         if(circles[i][0] - circles[i][2] <= 0) touch[0] = 1;
         if(circles[i][0] + circles[i][2] >= X) touch[2] = 1;

         if(circles[i][1] - circles[i][2] <= 0) touch[1] = 1;
         if(circles[i][1] + circles[i][2] >= Y) touch[3] = 1;

        for(auto child : dp[i])
        {
            if(vis[child] == 0)
            {
                dsu(child,X,Y,vis,dp,circles,touch);
            }
        }

    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<vector<int>> dp(n);
        for(int i= 0;i<n;i++)
        {
            for(int j = i+1;j<n;j++)
            {

                ll dc = (circles[i][0] - circles[j][0] +0ll )*(circles[i][0] - circles[j][0]) + 
                        (circles[i][1] - circles[j][1] +0ll)*(circles[i][1] - circles[j][1]);

                ll dr = (circles[i][2] + circles[j][2] +0ll)*(circles[i][2] + circles[j][2]);

                if(dc <= dr) {dp[i].push_back(j); dp[j].push_back(i);}                
            }
        }

        vector<bool> vis(n,0);
        for(int i = 0;i<n;i++)
        {
            if(vis[i] == 0)
            {
                vector<int> touch(4,0);
                dsu(i,X,Y,vis,dp,circles,touch); 
                if(touch[0] and touch[1]) return 0;
                if(touch[0] and touch[2]) return 0;
                if(touch[1] and touch[3]) return 0;
                if(touch[2] and touch[3]) return 0;

            }

        }
        return 1;


    }
};
