class Solution {

    private boolean touchBottomOrRight(int[][] circles, int i, int X) {
        int x = circles[i][0], y = circles[i][1], r = circles[i][2];
        return (y - r <= 0 || x + r >= X);
    }

    private boolean touchLeftOrTop(int[][] circles, int i, int Y) {
        int x = circles[i][0], y = circles[i][1], r = circles[i][2];
        return (x - r <= 0 || y + r >= Y);
    }

    private boolean isConnected(List<List<Integer>> graph, Set<Integer> startSet, Set<Integer> endSet, boolean[] visited) {
        Queue<Integer> q = new LinkedList<>();
        for (int s : startSet) {
            q.offer(s);
            visited[s] = true;
        }

        while (!q.isEmpty()) {
            int node = q.poll();

            if (endSet.contains(node)) return true;

            for (int neighbor : graph.get(node)) {
                if (!visited[neighbor]) {
                    visited[neighbor] = true;
                    q.offer(neighbor);
                }
            }
        }
        return false;
    }

     private boolean circlesTouch(int[][] circles, int i, int j) {

        long x1 = circles[i][0], y1 = circles[i][1], r1 = circles[i][2];
        long x2 = circles[j][0], y2 = circles[j][1], r2 = circles[j][2];
        long dx = x1 - x2, dy = y1 - y2;
        long distsq = dx * dx + dy * dy;
        long radsumsq = (r1 + r2) * (r1 + r2);
        return distsq <= radsumsq;
    }
    public boolean canReachCorner(int X, int Y, int[][] circles) {
        int n = circles.length;
        List<List<Integer>> graph = new ArrayList<>();

        for (int i = 0; i < n; ++i) {
            graph.add(new ArrayList<>());
        }

        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                if (circlesTouch(circles, i, j)) {
                    graph.get(i).add(j);
                    graph.get(j).add(i);
                }
            }
        }

        Set<Integer> btmRightTouch = new HashSet<>();
        Set<Integer> leftTopTouch = new HashSet<>();

        for (int i = 0; i < n; ++i) {
            if (touchBottomOrRight(circles, i, X)) {
                btmRightTouch.add(i);
            }
            if (touchLeftOrTop(circles, i, Y)) {
                leftTopTouch.add(i);
            }
        }

        boolean[] visited = new boolean[n];

        if (!btmRightTouch.isEmpty() && !leftTopTouch.isEmpty() && isConnected(graph, btmRightTouch, leftTopTouch, visited)) {
            return false;
        }

        return true;
    }




}
