class Solution:
    def bfs(self, g: List[List[int]], s: int, t1: int, t2: int, n: int) -> int:
        q = deque([s])
        v = [0] * (n + 4)
        v[s] = 1
        while q:
            u = q.popleft()
            while g[u]:
                x = g[u].pop()
                if not v[x]:
                    q.append(x)
                    v[x] = 1
        return v[t1] or v[t2]

    def fun2(self, c: List[List[int]], g: List[List[int]], i: int, j: int) -> None:
        x1, y1, r1 = c[i]
        x2, y2, r2 = c[j]
        d = ((x1 - x2) ** 2 + (y1 - y2) ** 2) ** 0.5
        rs = r1 + r2
        if rs >= d:
            g[i].append(j)
            g[j].append(i)    

    def fun1(self, c: List[List[int]], g: List[List[int]], i: int, X: int, Y: int) -> None:
        x, y, r = c[i]
        if x <= r:
            g[len(c)].append(i)
            g[i].append(len(c))
        if y <= r:
            g[len(c) + 1].append(i)
            g[i].append(len(c) + 1)    
        if X - x <= r:
            g[len(c) + 2].append(i)
            g[i].append(len(c) + 2)
        if Y - y <= r:
            g[len(c) + 3].append(i)
            g[i].append(len(c) + 3)



    def fun3(self, c: List[List[int]], g: List[List[int]], X: int, Y: int) -> None:
        i = 0
        while i < len(c):
            self.fun1(c, g, i, X, Y)
            j = i + 1
            while j < len(c):
                self.fun2(c, g, i, j)
                j += 1
            i += 1

    def fun4(self, g: List[List[int]], nc: int, nodes: int) -> int:
        return self.bfs(g, nc, nc + 1, nc + 2, nodes)

    def fun5(self, g: List[List[int]], nc: int, nodes: int) -> int:
        return self.bfs(g, nc + 3, nc + 2, nc + 1, nodes)

    def canReachCorner(self, X: int, Y: int, c: List[List[int]]) -> bool:
        nc = len(c)
        g = [[] for _ in range(nc + 4)]
        self.fun3(c, g, X, Y)
        blocked = max(self.fun4(g, nc, nc + 4), self.fun5(g, nc, nc + 4))
        return not blocked
