class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:


        def inside(i):
            x,y,r = circles[i]
            if 0 <= x <= X:
                if -r <= y <= Y + r:
                    return True
            if 0 <= y <= Y:
                if -r <= x <= X + r:
                    return True

            if (x-X)**2 + (y-Y)**2 <= r**2:
                return True
            if (x-X)**2 + y**2 <= r**2:
                return True
            if x**2 + (y-Y)**2 <= r**2:
                return True
            if x**2 + y**2 <= r**2:
                return True

            return False






        def update(i):

            ### l,r,u,d
            x,y,r = c2[i]

            ### l
            if abs(x) <= r:
                if 0 <= y <= Y:
                    touch[0] = True
                if x**2 + y**2 <= r**2:
                    touch[0] = True
                if x**2 + (y-Y)**2 <= r**2:
                    touch[0] = True

            ### r
            if abs(x-X) <= r:
                if 0 <= y <= Y:
                    touch[1] = True
                if (x-X)**2 + y**2 <= r**2:
                    touch[1] = True
                if (x-X)**2 + (y-Y)**2 <= r**2:
                    touch[1] = True

            ### u
            if abs(y-Y) <= r:
                if 0 <= x <= X:
                    touch[2] = True
                if x**2 + (y-Y)**2 <= r**2:
                    touch[2] = True
                if (x-X)**2 + (y-Y)**2 <= r**2:
                    touch[2] = True

            ### d
            if abs(y) <= r:
                if 0 <= x <= X:
                    touch[3] = True
                if x**2 + y**2 <= r**2:
                    touch[3] = True
                if (x-X)**2 + y**2 <= r**2:
                    touch[3] = True






        c2 = []

        for i, (x,y,r) in enumerate(circles):
            if x**2 + y**2 <= r**2:
                return False
            if (x-X)**2 + (y-Y)**2 <= r**2:
                return False
            if inside(i):
                c2.append((x,y,r))






        d = defaultdict(list)
        leng = len(c2)
        vis = set()

        for i in range(leng - 1):
            x1, y1, r1 = circles[i]
            for j in range(i+1, leng):
                x2, y2, r2 = circles[j]
                if (x1-x2)**2 + (y1-y2)**2 <= (r1+r2)**2:
                    d[i].append(j)
                    d[j].append(i)

        # if not d:
        #     return True

        for i in range(leng):
            if i in vis:
                continue

            stack = [i]
            vis.add(i)

            ### l,r,u,d
            touch = [False for _ in range(4)]

            while stack:
                cur = stack.pop(0)
                update(cur)

                for nxt in d[cur]:
                    if nxt in vis:
                        continue
                    vis.add(nxt)
                    stack.append(nxt)

            if touch[0] and touch[1]:
                return False
            if touch[0] and touch[3]:
                return False
            if touch[2] and touch[3]:
                return False
            if touch[2] and touch[1]:
                return False

        return True
