func funBFS(sillyGraph [][]int, start, wacky1, wacky2, loopy int) int {
	clownQueue := []int{start}
	markSilly := make([]int, loopy+4)
	markSilly[start] = 1

	for len(clownQueue) > 0 {
		u := clownQueue[0]
		clownQueue = clownQueue[1:]
		for _, v := range sillyGraph[u] {
			if markSilly[v] == 0 {
				clownQueue = append(clownQueue, v)
				markSilly[v] = 1
			}
		}
	}
	if markSilly[wacky1] == 1 || markSilly[wacky2] == 1 {
		return 1
	}
	return 0
}

func canReachCorner(X int, Y int, circles [][]int) bool {
	circleCount := len(circles)
	sillyGraph := make([][]int, circleCount+4)

	for i := 0; i < circleCount; i++ {
		if circles[i][0] <= circles[i][2] {
			sillyGraph[circleCount] = append(sillyGraph[circleCount], i)
			sillyGraph[i] = append(sillyGraph[i], circleCount)
		}
		if X-circles[i][0] <= circles[i][2] {
			sillyGraph[circleCount+2] = append(sillyGraph[circleCount+2], i)
			sillyGraph[i] = append(sillyGraph[i], circleCount+2)
		}
		if circles[i][1] <= circles[i][2] {
			sillyGraph[circleCount+1] = append(sillyGraph[circleCount+1], i)
			sillyGraph[i] = append(sillyGraph[i], circleCount+1)
		}
		if Y-circles[i][1] <= circles[i][2] {
			sillyGraph[circleCount+3] = append(sillyGraph[circleCount+3], i)
			sillyGraph[i] = append(sillyGraph[i], circleCount+3)
		}
		for j := i + 1; j < circleCount; j++ {
			distance := math.Sqrt(math.Pow(float64(circles[i][0]-circles[j][0]), 2) + math.Pow(float64(circles[i][1]-circles[j][1]), 2))
			radiusSum := circles[i][2] + circles[j][2]
			if float64(radiusSum) >= distance {
				sillyGraph[i] = append(sillyGraph[i], j)
				sillyGraph[j] = append(sillyGraph[j], i)
			}
		}
	}

	loopy := 0
	loopy = int(math.Max(float64(loopy), float64(funBFS(sillyGraph, circleCount, circleCount+1, circleCount+2, circleCount+4))))
	loopy = int(math.Max(float64(loopy), float64(funBFS(sillyGraph, circleCount+3, circleCount+2, circleCount+1, circleCount+4))))

	return loopy == 0
}
