class Solution {
public:
    int bfs(vector<vector<int>>&graph, int start, int a, int b, int nodes){
        vector<int>vis(nodes+4,0);
        queue<int>q;
        q.push(start);
        vis[start]=1;
        while(!q.empty()){
            int nd= q.front();
            q.pop();
            for(auto it:graph[nd]){
                if(!vis[it]){
                    vis[it]=1;
                    q.push(it);
                }
            }
        }
        return vis[a] || vis[b];
    }


    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n=circles.size();
        vector<pair<pair<int,int>,int>>cr(n);
        vector<vector<int>>graph(n+4);
        for(int i=0;i<n;i++){
            cr[i]= {{circles[i][0],circles[i][1]},circles[i][2]};
        }
        for(int i=0;i<n;i++){
            if(cr[i].first.first<=cr[i].second){
                graph[n].push_back(i);
                graph[i].push_back(n);
            }
            if(cr[i].first.second<=cr[i].second){
                graph[n+1].push_back(i);
                graph[i].push_back(n+1);
            }
            if(X-cr[i].first.first<=cr[i].second){
                graph[n+2].push_back(i);
                graph[i].push_back(n+2);
            }
            if(Y-cr[i].first.second<=cr[i].second){
                graph[n+3].push_back(i);
                graph[i].push_back(n+3);
            }
            for(int j=i+1;j<n;j++){
                double dist= sqrt(pow(cr[i].first.first-cr[j].first.first,2)+pow(cr[i].first.second-cr[j].first.second,2));
                int rad= cr[i].second+cr[j].second;
                if(rad>=dist){
                    graph[i].push_back(j);
                    graph[j].push_back(i);
                }
            }
        }
        int b=0;
        b= max(b, bfs(graph,n,n+1,n+2,n+4));
        b= max(b, bfs(graph,n+3,n+2,n+1,n+4));
        if(b) return false;
        else return true ;
    }
};
