    int traverse(vvi &g, int s, int t, int e, int n) {
    queue<int> q;
    vi vis(n + 10, 0);
    q.push(s);
    vis[s] = 1;

    while (!q.empty()) {
        auto front = q.front();
        q.pop();
        for (int nbr : g[front]) {
            if (!vis[nbr]) {
