class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();

        auto collide = [&] (int i, int j) -> bool {
            if (i >= n && j >= n)
                return false;

            if (i < n && j < n) {
                // two circles
                long long dis = 1LL * (circles[i][0] - circles[j][0]) * (circles[i][0] - circles[j][0]) + 1LL * (circles[i][1] - circles[j][1]) * (circles[i][1] - circles[j][1]);
                long long rad = 1LL * (circles[i][2] + circles[j][2]) * (circles[i][2] + circles[j][2]);
                return dis <= rad;
            }

            int c, o;
            if (i >= n) {
                o = i;
                c = j;
            } else {
                o = j;
                c = i;
            }

            if (o == n) {
                // lower right corner
                if (circles[c][0] + circles[c][2] >= X)
                    return true;
                if (circles[c][1] - circles[c][2] <= 0)
                    return true;
                return false;
            } else {
                // upper left corner
                if (circles[c][0] - circles[c][2] <= 0)
                    return true;
                if (circles[c][1] + circles[c][2] >= Y)
                    return true;
                return false;
            }

        };

        vector<int> fa(n + 2);
        auto findfa = [&] (auto &&findfa, int x) -> int {
            if (fa[x] != x)
                fa[x] = findfa(findfa, fa[x]);
            return fa[x];
        };
        for (auto i = 0; i <= n + 1; i++)
            fa[i] = i;

        for (auto i = 0; i <= n + 1; i++)
            for (auto j = 0; j <= n + 1; j++) {
                if (i == j)
                    continue;
                if (collide(i, j))
                    fa[findfa(findfa, i)] = findfa(findfa, j);
            }

        if (findfa(findfa, n) == findfa(findfa, n + 1))
            return false;
        else
            return true;
    }
};
