import java.util.*;

class Solution {

    public int bfs(List<List<Integer>> graph, int start, int target1, int target2, int nodes) {
        Queue<Integer> queue = new LinkedList<>();
        boolean[] visited = new boolean[nodes + 4];
        queue.add(start);
        visited[start] = true;

        while (!queue.isEmpty()) {
            int u = queue.poll();
            for (int v : graph.get(u)) {
                if (!visited[v]) {
                    queue.add(v);
                    visited[v] = true;
                }
            }
        }
        return visited[target1] || visited[target2] ? 1 : 0;
    }

    public boolean canReachCorner(int X, int Y, int[][] circlesArray) {
        List<int[]> circles = new ArrayList<>();
        List<List<Integer>> graph = new ArrayList<>(circlesArray.length + 4);
        int numCircles = circlesArray.length;

        for (int i = 0; i < numCircles; i++) {
            circles.add(new int[]{circlesArray[i][0], circlesArray[i][1], circlesArray[i][2]});
            graph.add(new ArrayList<>());
        }

        graph.add(new ArrayList<>()); // For bottom-left corner
        graph.add(new ArrayList<>()); // For top-right corner
        graph.add(new ArrayList<>()); // For right border
        graph.add(new ArrayList<>()); // For top border

        int i = 0;
        while (i < numCircles) {
            if (circles.get(i)[0] <= circles.get(i)[2]) {
                graph.get(numCircles).add(i);
                graph.get(i).add(numCircles);
            }
            if (X - circles.get(i)[0] <= circles.get(i)[2]) {
                graph.get(numCircles + 2).add(i);
                graph.get(i).add(numCircles + 2);
            }
            if (circles.get(i)[1] <= circles.get(i)[2]) {
                graph.get(numCircles + 1).add(i);
                graph.get(i).add(numCircles + 1);
            }
            if (Y - circles.get(i)[1] <= circles.get(i)[2]) {
                graph.get(numCircles + 3).add(i);
                graph.get(i).add(numCircles + 3);
            }
            int j = i + 1;
            while (j < numCircles) {
                double distance = Math.sqrt(Math.pow(circles.get(i)[0] - circles.get(j)[0], 2) +
                                            Math.pow(circles.get(i)[1] - circles.get(j)[1], 2));
                int radiusSum = circles.get(i)[2] + circles.get(j)[2];
                if (radiusSum >= distance) {
                    graph.get(i).add(j);
                    graph.get(j).add(i);
                }
                j++;
            }
            i++;
        }

        int isBlocked = 0;
        isBlocked = Math.max(isBlocked, bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4)); // Check for bottom-left corner
        isBlocked = Math.max(isBlocked, bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4)); // Check for top-right corner
        return isBlocked == 0;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        int X = 3, Y = 4;
        int[][] circles = {{2, 1, 1}};
        System.out.println(sol.canReachCorner(X, Y, circles)); 

        X = 3;
        Y = 3;
        circles = new int[][]{{1, 1, 2}};
        System.out.println(sol.canReachCorner(X, Y, circles)); 
    }
}
