class UnionFindSet:
    def __init__(self, n):
        self._parents = [i for i in range(n)]
        self._ranks = [1 for i in range(n)]

    def find(self, u):
        while u != self._parents[u]:
            self._parents[u] = self._parents[self._parents[u]]
            u = self._parents[u]
        return u

    def union(self, u, v):
        pu, pv = self.find(u), self.find(v)
        if pu == pv: return False

        if self._ranks[pu] < self._ranks[pv]:
            self._parents[pu] = pv
        elif self._ranks[pu] > self._ranks[pv]:
            self._parents[pv] = pu
        else:        
            self._parents[pv] = pu
            self._ranks[pu] += 1     
        return True

class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        arr = defaultdict(set)
        n = len(circles)
        for i in range(n):
            x, y, r = circles[i]
            if x - r <= 0:
                arr[i].add("left")
            if x + r >= X:
                arr[i].add("right")
            if y - r <= 0:
                arr[i].add("down")
            if y + r >= Y:
                arr[i].add("top")
        uf = UnionFindSet(n)
        for i in range(n):
            for j in range(i + 1, n):
                if (circles[i][0] - circles[j][0]) ** 2 + (circles[i][1] - circles[j][1]) ** 2 <= (circles[i][2] + circles[j][2]) ** 2:
                    uf.union(i, j)
        l = defaultdict(set)
        for i in range(n):
            index = uf.find(i)
            l[index] = l[index] | arr[i]
        #print(l)
        for index in l:
            if "left" in l[index] and "right" in l[index]:
                return False
            if "left" in l[index] and "down" in l[index]:
                return False
            if "right" in l[index] and "top" in l[index]:
                return False
            if "top" in l[index] and "down" in l[index]:
                return False
        return True
