#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define mod 1000000007
#define ff first
#define ss second
#define mk make_pair
#define pb push_back
#define rep(i, start, end) for (int i = start; i < end; i++)
#define ld long double
using ii = pair<int, int>;
const static int mx = 2e5 + 100;

class Solution {
public:
    vector<int>g[1005];
    vector<int>brr[1005];
    int vis[1005];
    set<int>st;
    void dfs(int node,int par)
    {
        vis[node]=1;
        for(auto e:brr[node])
        {
            st.insert(e);
        }
        for(auto e:g[node])
        {
            if(!vis[e])
            {
                dfs(e,node);
            }
        }
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& arr) {
        int n=(int)arr.size();
        rep(i,0,n)
        {
            g[i].clear();
            brr[i].clear();
            vis[i]=0;
        }
        rep(i,0,n)
        {
            ll a=arr[i][0],b=arr[i][1],r=arr[i][2];
            ll val=r*1LL*r-a*1LL*a;
            if(val>=0)
            {
                brr[i].pb(3);
            }
            val=r*1LL*r-b*1LL*b;
            if(val>=0)
            {
                brr[i].pb(0);
            }
            val=(X-a)*1LL*(X-a);
            if(r*1LL*r>=val)
            {
                brr[i].pb(1);
            }
            val=(Y-b)*1LL*(Y-b);
            if(r*1LL*r>=val)
            {
                brr[i].pb(2);
            }
        }
        // rep(i,0,n)
        // {
        //     cout<<i<<"\n";
        //     for(auto e:brr[i])
        //     {
        //         cout<<e<<" ";
        //     }
        //     cout<<"\n";
        // }
        rep(i,0,n)
        {
            rep(j,i+1,n)
            {
                ll d=(arr[i][0]-arr[j][0])*1LL*(arr[i][0]-arr[j][0]);
                d+=(arr[i][1]-arr[j][1])*1LL*(arr[i][1]-arr[j][1]);
                ll r=(arr[i][2]+arr[j][2])*1LL*(arr[i][2]+arr[j][2]);
                if(d<=r)
                {
                    g[i].pb(j);
                    g[j].pb(i);
                }
            }
        }
        rep(i,0,n)
        {
            if(!vis[i])
            {
                st.clear();
                dfs(i,-1);
                if(st.size()>=3)
                {
                    return false;
                }
                if(st.size()==2)
                {
                    int a=*st.begin(),b=*st.rbegin();
                    if(b-a>=2)
                    {
                        return false;
                    }
                    if(a==1&&b==2)
                    {
                        return false;
                    }
                }
            }
        }
        return true;
    }
};
