class Solution:
    def bfs(self, graph, start, target1, target2, nodes):
        q = deque([start])
        visited = [0] * (nodes + 4)
        visited[start] = 1

        while q:
            u = q.popleft()
            for v in graph[u]:
                if not visited[v]:
                    q.append(v)
                    visited[v] = 1

        return visited[target1] or visited[target2]

    def canReachCorner(self, X, Y, circles2):
        circles = [((c[0], c[1]), c[2]) for c in circles2]
        num_circles = len(circles2)
        graph = [[] for _ in range(num_circles + 4)]

        for i in range(num_circles):
            x, y, r = circles2[i]

            if x <= r:
                graph[num_circles].append(i)
                graph[i].append(num_circles)
            if X - x <= r:
                graph[num_circles + 2].append(i)
                graph[i].append(num_circles + 2)
            if y <= r:
                graph[num_circles + 1].append(i)
                graph[i].append(num_circles + 1)
            if Y - y <= r:
                graph[num_circles + 3].append(i)
                graph[i].append(num_circles + 3)

            for j in range(i + 1, num_circles):
                x2, y2, r2 = circles2[j]
                distance = sqrt((x - x2) ** 2 + (y - y2) ** 2)
                radius_sum = r + r2
                if radius_sum >= distance:
                    graph[i].append(j)
                    graph[j].append(i)

        is_blocked = False
        is_blocked = is_blocked or self.bfs(graph, num_circles, num_circles + 1, num_circles + 2, num_circles + 4)  # Check for bottom-left corner
        is_blocked = is_blocked or self.bfs(graph, num_circles + 3, num_circles + 2, num_circles + 1, num_circles + 4)  # Check for top-right corner

        return not is_blocked
