class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        C = len(circles)
        adj = [[] for _ in range(C)]
        circles.sort()
        left, top, bottom, right = set(),set(),set(),set()

        for i in range(C):
            x,y,r = circles[i]
            for j in range(i+1,C):
                x2,y2,r2 = circles[j]
                if (r-r2)**2 <= (x-x2)**2 + (y-y2)**2 <= (r+r2)**2:
                    adj[i].append(j)
                    adj[j].append(i)
            if x + r >= X:
                right.add(i)
            if x - r <= 0:
                left.add(i)
            if y + r >= Y:
                top.add(i)
            if y - r <= 0:
                bottom.add(i)


        for ci in left:
            seen = set()
            if ci in right or ci in bottom:
                return False

            level = [ci]
            while level:
                next_level = []

                for c in level:

                    seen.add(c)
                    for c2 in adj[c]:
                        if c2 in right or c2 in bottom:
                            return False
                        if c2 in seen:
                            continue
                        seen.add(c2)
                        next_level.append(c2)
                level = next_level
        for ci in top:
            seen = set()
            if ci in right or ci in bottom:
                return False

            level = [ci]
            while level:
                next_level = []

                for c in level:

                    seen.add(c)
                    for c2 in adj[c]:
                        if c2 in right or c2 in bottom:
                            return False
                        if c2 in seen:
                            continue
                        seen.add(c2)
                        next_level.append(c2)
                level = next_level

        return True
