class Solution {
public:
    #define ll long long int

    // Circle Intersect?
    bool ci(vector<int> &c1, vector<int> &c2){
        ll x = (c1[0] - c2[0]), y = (c1[1] - c2[1]);
        double dis = sqrtl((x * x) + (y * y));
        return (double)(c1[2] + c2[2]) >= dis;
    }

    // BFS
    bool bfs(ll src, ll dest, vector<vector<ll>> &adj){
        if (src == dest)
            return 0;
        vector<bool> vis((ll)(adj.size()), 0);
        queue<ll> q;
        q.push(src);
        while (q.size()){
            ll val = q.front();
            q.pop();
            vis[val] = 1;
            for (auto x : adj[val]){
                if (!vis[x]){
                    if (x == dest)
                        return 0;
                    q.push(x);
                }
            }
        }
        return 1;
    }


    bool canReachCorner(int x, int y, vector<vector<int>>& c) {
        ll n = c.size();

        vector<vector<ll>> adj(n);
        for (ll i = 0; i < n; i++){
            for (ll j = i + 1; j < n; j++){
                if (ci(c[i], c[j])){
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }

        vector<ll> up, dw, lf, ri;

        for (ll i = 0; i < n; i++){
            if (c[i][1] + c[i][2] >= y)
                up.push_back(i);
        }
        for (ll i = 0; i < n; i++){
            if (c[i][1] - c[i][2] <= 0)
                dw.push_back(i);
        }
        for (ll i = 0; i < n; i++){
            if (c[i][0] - c[i][2] <= 0)
                lf.push_back(i);
        }

        for (ll i = 0; i < n; i++){
            if (c[i][0] + c[i][2] >= x)
                ri.push_back(i);
        }


        // possible combinatoins  --  UD, UR, RL, LD
        for (auto x : up){
            for (auto y : dw){
                if (!bfs(x, y, adj)){
                    return false;
                }
            }
            for (auto y : ri){
                if (!bfs(x, y, adj)){
                    return false;
                }
            }
        }

        for (auto x : lf){
            for (auto y : ri){
                if (!bfs(x, y, adj)){
                    return false;
                }
            }
            for (auto y : dw){
                if (!bfs(x, y, adj)){
                    return false;
                }
            }
        }

        return true;
    }
};
