    int find(int x, int *par) {

        if(par[x]!=x) {
            par[x]=find(par[x],par);
        }
        return par[x];
    }
    void Union(int x,int y,int *par,int *r) {
        int u=find(x, par);
        int v=find(y, par);
        if(u!=v) {
            if(r[u]>r[v]) {
                par[v]=u;
            }else if(r[u]<r[v]) {
                par[u]=v;
            }else {
                par[v]=u;
                r[u]++;
            }
        }
    }

