class Solution {
public:
     int harshitHelper(int harshitMain, int harshitA, int harshitB, int harshitN, vector<int> harshitAdj[]) {
        queue<int> harshitQ;
        vector<int> harshitVis(harshitN + 16, 0);
        harshitQ.push(harshitMain);
        harshitVis[harshitMain] = 1;
        while (harshitQ.size()) {
            int harshitK = harshitQ.front();
            harshitQ.pop();
            for (auto harshitU : harshitAdj[harshitK]) {
                if (!harshitVis[harshitU]) {
                    harshitVis[harshitU] = 1;
                    harshitQ.push(harshitU);
                }
            }
        }
        return harshitVis[harshitA] || harshitVis[harshitB];     
    }
    bool canReachCorner(int harshitX, int harshitY, vector<vector<int>>& harshitCircles) {
        int harshitN = harshitCircles.size();
        vector<vector<int>> harshitC(harshitN);
        vector<int> harshitAdj[harshitN + 16];
        harshitC = harshitCircles;
        for (int harshitI = 0; harshitI < harshitN; harshitI++) {
            if (harshitC[harshitI][1] <= harshitC[harshitI][2]) {
                harshitAdj[harshitN + 1].push_back(harshitI);
                harshitAdj[harshitI].push_back(harshitN + 1);
            }
            if (harshitC[harshitI][0] <= harshitC[harshitI][2]) {
                harshitAdj[harshitN].push_back(harshitI);
                harshitAdj[harshitI].push_back(harshitN);
            }
            if (harshitX - harshitC[harshitI][0] <= harshitC[harshitI][2]) {
                harshitAdj[harshitN + 2].push_back(harshitI);
                harshitAdj[harshitI].push_back(harshitN + 2);
            }
            if (harshitY - harshitC[harshitI][1] <= harshitC[harshitI][2]) {
                harshitAdj[harshitN + 3].push_back(harshitI);
                harshitAdj[harshitI].push_back(harshitN + 3);
            }
            for (int harshitJ = harshitI + 1; harshitJ < harshitN; harshitJ++) {
                double harshitOk = sqrt(pow(harshitC[harshitI][0] - harshitC[harshitJ][0], 2) + pow(harshitC[harshitI][1] - harshitC[harshitJ][1], 2));
                if (harshitC[harshitI][2] + harshitC[harshitJ][2] >= harshitOk) {
                    harshitAdj[harshitJ].push_back(harshitI);
                    harshitAdj[harshitI].push_back(harshitJ);
                }
            }
        }
        int harshitA = harshitHelper(harshitN, harshitN + 1, harshitN + 2, harshitN + 4, harshitAdj);
        int harshitB = harshitHelper(harshitN + 3, harshitN + 2, harshitN + 1, harshitN + 4, harshitAdj);
        return !(harshitA || harshitB);
    }
};
