class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& z) {
        x = X, y = Y;
        n = z.size();
        z.insert(z.begin(), {0,0,0});
        rep(i,1,n){
            p[i] = i;
            mask[i] = 0;
            if(z[i][0]-z[i][2]<=0)mask[i]|= 1;
            if(z[i][0]+z[i][2]>=x)mask[i]|= 4;
            if(z[i][1]-z[i][2]<=0)mask[i]|= 8;
            if(z[i][1]+z[i][2]>=y)mask[i]|= 2;
            // cout<<z[i][0]<<" "<<z[i][1]<<" "<<z[i][2]<<" = "<<mask[i]<<endl;
        }
        rep(i,1,n)rep(j,i+1,n){
            a = par(i);
            b = par(j);
            if(a==b)continue;
            dx = z[i][0]-z[j][0];
            dy = z[i][1]-z[j][1];
            R = z[i][2]+z[j][2];
            if(dx*dx+dy*dy<=R*R){
                p[a] = b;
                mask[b]|= mask[a];
            }
        }
        rep(i,1,n)if((mask[i]&5)==5 || (mask[i]&10)==10 || (mask[i]&6)==6 || (mask[i]&9)==9)return 0;
        return 1;
    }
};
