class Solution {

    bool bfs(int node,vector<int>&v, vector<vector<int>>&adj, vector<int>&vis){
        set<int>s;
        s.insert(v[node]);

        queue<int>q;
        q.push(node);
        vis[node]=1;

        while(!q.empty()){
            int t=q.front();
            q.pop();

            for(auto i:adj[t]){
                if(vis[i]==0){
                    q.push(i);
                    vis[i]=1;
                    s.insert(v[i]);
                }
            }
        }

        if(s.find(1)!=s.end()){
            if(s.find(-1)!=s.end())return 1;
            if(s.find(2)!=s.end())return 1;
        }

         if(s.find(-2)!=s.end()){
            if(s.find(-1)!=s.end())return 1;
            if(s.find(2)!=s.end())return 1;
        }

        return 0;
    }
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
            int n=circles.size();

            vector<int>v(n,0);

            for(auto i:circles){
                if(i[0]<=i[2] && i[0]+0LL+i[2]>=X)return false;
                if(i[1]<=i[2] && i[1]+0LL+i[2]>=Y)return false;

                if(i[0]<=i[2] && i[1]<=i[2])return false;
                if(i[0]+0LL+i[2]>=X && i[1]+0LL+i[2]>=Y)return false;


            }

        for(int i=0;i<n;i++){
            if(circles[i][0]<=circles[i][2])v[i]=-1;//touch left
             if(circles[i][1]<=circles[i][2])v[i]=-2;//bottom

            if(circles[i][0]+0LL+circles[i][2]>=X)v[i]=1;
            if(circles[i][1]+0LL+circles[i][2]>=Y)v[i]=2;
        }

        vector<vector<int>>adj(n);

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                long long r=(circles[i][2]+0LL+circles[j][2])*(circles[i][2]+0LL+circles[j][2]);

                int x1=circles[i][0];
                int y1=circles[i][1];

                 int x2=circles[j][0];
                int y2=circles[j][1];

                if((x1+0LL-x2)*(x1+0LL-x2)+(y1+0LL-y2)*(y1+0LL-y2)<=r){
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }

        vector<int>vis(n,0);

        for(int i=0;i<n;i++){
            if(vis[i]==0){

                bool check=bfs(i,v,adj,vis);

                if(check)return 0;
            }
        }

        return 1;

    }
};
