class Solution {
public:
bool intersect(int f,int s,vector<vector<int>>&lamp){
	int x1 = lamp[f][0];
	int y1 = lamp[f][1];
	int r1 = lamp[f][2];
	int x2 = lamp[s][0];
	int y2 = lamp[s][1];
	int r2 = lamp[s][2];
 long long sum=(x1-x2);
 sum=sum*(sum);
long long sum2=(y1-y2);
sum2=sum2*sum2;
sum+=sum2;
	return (sqrt(sum) <= r1+r2);
}
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n=circles.size();
        vector<vector<int>>gr(n+5);
		for(int i = 0; i < n; i++){
			for(int j = i+1; j < n; j++){
				if(intersect(i,j,circles)){
					gr[i+1].push_back(j+1);
					gr[j+1].push_back(i+1);
				}
			}
		}
        for(int i = 0; i < n; i++){
			if(circles[i][0] <= circles[i][2]){
				gr[i+1].push_back(0);
				gr[0].push_back(i+1);
			}

			if(circles[i][1] <= circles[i][2]){
				gr[i+1].push_back(n+1);
				gr[n+1].push_back(i+1);
			}
            if(X-circles[i][0]<=circles[i][2]){
                gr[n+2].push_back(i+1);
                gr[i+1].push_back(n+2);
            }
              if(Y-circles[i][1]<=circles[i][2]){
                gr[n+3].push_back(i+1);
                gr[i+1].push_back(n+3);
            }

		}
        vector<int>vis(n+4,false);
		queue<int>q;
		q.push(0);
		bool chk = false;
		while (q.size())
		{
			int front = q.front();
			q.pop();
			if(front==n+1||front==n+2){
				chk = true;
				break;
			}
			for(auto ch:gr[front]){
				if(vis[ch]==false){
					vis[ch] = true;
					q.push(ch);
				}
			}
		}
        if(chk) return false;
         vector<int>vis2(n+4,false);
		queue<int>q2;
		q2.push(n+3);

		while (q2.size())
		{
			int front = q2.front();
			q2.pop();
			if(front==n+1||front==n+2){
				chk = true;
				break;
			}
			for(auto ch:gr[front]){
				if(vis2[ch]==false){
					vis2[ch] = true;
					q2.push(ch);
				}
			}
		}
        if(chk)return false;
        return true;
    }
};
