class Solution {
public:
    struct UF {
        vector<int> e;
        UF(int n) : e(n, -1) {}
        bool sameSet(int a, int b) { return find(a) == find(b); }
        int size(int x) { return -e[find(x)]; }
        int find(int x) { return e[x] < 0 ? x : e[x] = find(e[x]); }
        bool join(int a, int b) {
            a = find(a), b = find(b);
            if (a == b) return false;
            if (e[a] > e[b]) swap(a, b);
            e[a] += e[b]; e[b] = a;
            return true;
        }
    };
    struct pt {
        long long x,y;
    };
    long long dist(pt a, pt b) {
        return (a.x-b.x)*(a.x-b.x) + (a.y-b.y) * (a.y-b.y);
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& nums) {
        UF u = UF(nums.size()+4);
        //0 is leftmost
        //1 is rightmost
        //2 is top
        //3 is bottom
        int n = nums.size();
        for (int i = 0; i < n; i ++) {
            int rd = nums[i][2];
            pt l = pt(nums[i][0]-rd, nums[i][1]);
            pt r = pt(nums[i][0] + rd, nums[i][1]);
            pt tp = pt(nums[i][0], nums[i][1] + rd);
            pt bt = pt(nums[i][0], nums[i][1] - rd);
            if (l.x <= 0) u.join(0,i+4);
            if (r.x >= X) u.join(1,i+4);
            if (tp.y >= Y) u.join(2,i+4);
            if (bt.y <= 0) u.join(3,i+4);
        }
        for (int i = 0; i < n; i ++) {
            for (int j = 0; j < n; j ++) {
                if (dist(pt(nums[i][0], nums[i][1]), pt(nums[j][0], nums[j][1])) <= ((long long)nums[i][2] + nums[j][2]) * ((long long)nums[i][2] + nums[j][2])) {
                    u.join(i+4,j+4);
                }
            }
        }
        //u.join(0,2); u.join(3,1);
        //if ((u.sameSet(0,1) && !u.sameSet(2,) && u.sameSet(2,3)) return false;
        if (u.sameSet(0,1) || u.sameSet(2,3) || u.sameSet(2,1) || u.sameSet(0,3)) return false;
        //if (u.sameSet(0,1)) return false;
        return true;
    }
};
