class Solution {
public:
    int bfs(vector<vector<int>>& g, int s, int t1, int t2, int n) {
        queue<int> q;
        vector<bool> v(n + 4, false);
        q.push(s);
        v[s] = true;

        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int x : g[u]) {
                if (!v[x]) {
                    q.push(x);
                    v[x] = true;
                }
            }
        }
        return v[t1] || v[t2] ? 1 : 0;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& c2) {
        vector<vector<int>> c;
        vector<vector<int>> g(c2.size() + 4);

        int n = c2.size();

        for (int i = 0; i < n; ++i) {
            c.push_back({c2[i][0], c2[i][1], c2[i][2]});
        }

        for (int i = 0; i < n + 4; i++) {
            g[i] = vector<int>();
        }

        for (int i = 0; i < n; ++i) {
            if (c[i][0] <= c[i][2]) {
                g[n].push_back(i);
                g[i].push_back(n);
            }
            if (X - c[i][0] <= c[i][2]) {
                g[n + 2].push_back(i);
                g[i].push_back(n + 2);
            }
            if (c[i][1] <= c[i][2]) {
                g[n + 1].push_back(i);
                g[i].push_back(n + 1);
            }
            if (Y - c[i][1] <= c[i][2]) {
                g[n + 3].push_back(i);
                g[i].push_back(n + 3);
            }
            for (int j = i + 1; j < n; ++j) {
                double d = sqrt(pow(c[i][0] - c[j][0], 2) + pow(c[i][1] - c[j][1], 2));
                int rs = c[i][2] + c[j][2];
                if (rs >= d) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        int b = 0;
        b = max(b, bfs(g, n, n + 1, n + 2, n + 4));
        b = max(b, bfs(g, n + 3, n + 2, n + 1, n + 4)); 

        return b == 0;
    }
};
