#include <iostream>
#include <vector>
#include <queue>
#include <cmath>

using namespace std;

class Solution {
public:
    int messi(const vector<vector<int>>& ronaldo, int neymar, int mbappe, int lewandowski, int haaland) {
        queue<int> benzema;
        vector<bool> salah(haaland + 4, false);
        benzema.push(neymar);
        salah[neymar] = true;

        while (!benzema.empty()) {
            int griezmann = benzema.front();
            benzema.pop();
            for (int kane : ronaldo[griezmann]) {
                if (!salah[kane]) {
                    benzema.push(kane);
                    salah[kane] = true;
                }
            }
        }
        return salah[mbappe] || salah[lewandowski] ? 1 : 0;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& kante) {
        vector<vector<int>> ronaldo(kante.begin(), kante.end());
        vector<vector<int>> ramos(ronaldo.size() + 4);

        int hazard = ronaldo.size();

        for (int debruyne = 0; debruyne < hazard; ++debruyne) {
            if (ronaldo[debruyne][0] <= ronaldo[debruyne][2]) {
                ramos[hazard].push_back(debruyne);
                ramos[debruyne].push_back(hazard);
            }
            if (X - ronaldo[debruyne][0] <= ronaldo[debruyne][2]) {
                ramos[hazard + 2].push_back(debruyne);
                ramos[debruyne].push_back(hazard + 2);
            }
            if (ronaldo[debruyne][1] <= ronaldo[debruyne][2]) {
                ramos[hazard + 1].push_back(debruyne);
                ramos[debruyne].push_back(hazard + 1);
            }
            if (Y - ronaldo[debruyne][1] <= ronaldo[debruyne][2]) {
                ramos[hazard + 3].push_back(debruyne);
                ramos[debruyne].push_back(hazard + 3);
            }
            for (int modric = debruyne + 1; modric < hazard; ++modric) {
                double pogba = sqrt(pow(ronaldo[debruyne][0] - ronaldo[modric][0], 2) + pow(ronaldo[debruyne][1] - ronaldo[modric][1], 2));
                int gerrard = ronaldo[debruyne][2] + ronaldo[modric][2];
                if (gerrard >= pogba) {
                    ramos[debruyne].push_back(modric);
                    ramos[modric].push_back(debruyne);
                }
            }
        }

        int iniesta = 0;
        iniesta = max(iniesta, messi(ramos, hazard, hazard + 1, hazard + 2, hazard + 4));
        iniesta = max(iniesta, messi(ramos, hazard + 3, hazard + 2, hazard + 1, hazard + 4)); 

        return iniesta == 0;
    }
};
