class Solution {
    public int[] parent;
    public int[][] area;
    public int find(int i){
        if(parent[i] == i){
            return i;
        }
        parent[i] =  find(parent[i]);
        return parent[i];
    }
    public void union(int u,int v){
        int up = find(u);
        int vp = find(v);

        parent[up] = vp;
        area[vp][0] = Math.max(area[vp][0],area[up][0]);
        area[vp][1] = Math.max(area[vp][1],area[up][1]);
        area[vp][2] = Math.min(area[vp][2],area[up][2]);
        area[vp][3] = Math.min(area[vp][3],area[up][3]);
    }
    // U R D L
    public boolean canReachCorner(int X, int Y, int[][] circles) {
        parent = new int[circles.length];
        area = new int[parent.length][4];
        for(int i =0;i<parent.length;i++){
            parent[i] = i;
            int circle[] = circles[i];
            area[i][0] = circle[2] + circle[1];
            area[i][1] = circle[2] + circle[0];
            area[i][2] = circle[1] - circle[2];
            area[i][3] = circle[0] - circle[2];
        }


        for(int i =0;i<circles.length;i++){
            for(int j =i+1;j<circles.length;j++){
                if(intersect(circles[i],circles[j]) && find(i) != find(j)){
                    union(i,j);
                }
            }
        }
        for(int i =0;i<parent.length;i++){
            if(parent[i] == i){
                if(check(X,Y, area[i])){
                    return false;
                }
            }
        }
        return true;
    }
    public boolean check(int x,int y, int[] a){
        if(a[0] >= y){
            if(a[1] >= x || a[2] <=0){
                return true;
            }
        }
        if(a[3] <=0){
             if(a[1] >= x || a[2] <=0){
                return true;
            }
        }
        return false;
    }
    public boolean intersect(int[] c1, int [] c2){
        double dist = Math.sqrt((c1[0]-c2[0])*(c1[0]-c2[0]) + (c1[1]-c2[1])*(c1[1]-c2[1]));
        if(dist<= c1[2] + c2[2]){
            return true;
        }
        return false;
    }
}
