class Solution {
public:
    int bfs(vector<vector<int>>& graph, int start, int target1, int target2, int totalNodes) {
        queue<int> q;
        vector<int> visited(totalNodes + 4, 0);
        q.push(start);
        visited[start] = 1;

        while (!q.empty()) {
            int current = q.front();
            q.pop();
            for (int neighbor: graph[current]) {
                if (!visited[neighbor]) {
                    q.push(neighbor);
                    visited[neighbor] = 1;
                }
            }
        }
        return visited[target1] or visited[target2];
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circlesData) {
        int numCircles = circlesData.size();
        vector<pair<pair<int, int>, int>> circles(numCircles);
        vector<vector<int>> graph(numCircles + 4);

        for (int i = 0; i < numCircles; ++i) {
            circles[i] = {{circlesData[i][0], circlesData[i][1]}, circlesData[i][2]};
        }

        for (int i = 0; i < numCircles; ++i) {
            if (circles[i].first.first <= circles[i].second) {
                graph[numCircles].push_back(i);
                graph[i].push_back(numCircles);
            }
            if (X - circles[i].first.first <= circles[i].second) {
                graph[numCircles + 2].push_back(i);
                graph[i].push_back(numCircles + 2);
            }
            if (circles[i].first.second <= circles[i].second) {
                graph[numCircles + 1].push_back(i);
                graph[i].push_back(numCircles + 1);
            }
            if (Y - circles[i].first.second <= circles[i].second) {
                graph[numCircles + 3].push_back(i);
                graph[i].push_back(numCircles + 3);
            }
            for (int j = i + 1; j < numCircles; ++j) {
                double distance = sqrt(pow(circles[i].first.first - circles[j].first.first, 2) +
                                       pow(circles[i].first.second - circles[j].first.second, 2));
                int radiusSum = circles[i].second + circles[j].second;
                if (radiusSum >= distance) {
                    graph[i].push_back(j);
                    graph[j].push_back(i);
                }
            }
        }

        int isBlocked = 0;
        isBlocked = max(isBlocked, bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4));
        isBlocked = max(isBlocked, bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4));

        return !isBlocked;
    }
};
