class Solution {
public:
    int b(vector<vector<int>>& g, int s, int t1, int t2, int n) {
        queue<int> q;
        vector<int> v(n + 4, 0);
        q.push(s);
        v[s] = 1;
        while (!q.empty()) {
            int u = q.front(); q.pop();
            for (int nei : g[u]) {
                if (!v[nei]) {
                    q.push(nei);
                    v[nei] = 1;
                }
            }
        }
        return v[t1] || v[t2];
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& c2) {
        vector<pair<pair<int, int>, int>> c(c2.size());
        vector<vector<int>> g(c2.size() + 4);
        int n = c2.size();
        for (int i = 0; i < n; ++i) {
            c[i].first.first = c2[i][0];
            c[i].first.second = c2[i][1];
            c[i].second = c2[i][2];
        }
        for (int i = 0; i < n; ++i) {
            if (c[i].first.first <= c[i].second) {
                g[n].push_back(i);
                g[i].push_back(n);
            }
            if (X - c[i].first.first <= c[i].second) {
                g[n + 2].push_back(i);
                g[i].push_back(n + 2);
            }
            if (c[i].first.second <= c[i].second) {
                g[n + 1].push_back(i);
                g[i].push_back(n + 1);
            }
            if (Y - c[i].first.second <= c[i].second) {
                g[n + 3].push_back(i);
                g[i].push_back(n + 3);
            }
            for (int j = i + 1; j < n; ++j) {
                double d = sqrt(pow(c[i].first.first - c[j].first.first, 2) + pow(c[i].first.second - c[j].first.second, 2));
                int rsum = c[i].second + c[j].second;
                if (rsum >= d) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }
        int b1 = 0;
        b1 = max(b1, b(g, n, n + 1, n + 2, n + 4));
        b1 = max(b1, b(g, n + 3, n + 2, n + 1, n + 4));
        return !b1;
    }
};
