class Solution {
public:
	int bfs(vector<vector<int>> &g, int start, int t1, int t2, int no) {
		queue<int> q;
		vector<int> vis(no + 4, 0);
		q.push(start);
		vis[start] = 1;
		while (!q.empty()) {
			int u = q.front();
			q.pop();
			for (int x : g[u]) {
				if (!vis[x]) {
					q.push(x);
					vis[x] = 1;
				}
			}
		}
		return vis[t1] or vis[t2];
	}

	bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
		int n=circles.size();
		int f = 0;
		vector<vector<int>> g(n+4);
		vector<pair<pair<int, int>, int>> circle(n);
		for (int i = 0; i < n; ++i) {
			circle[i].second=circles[i][2];
			circle[i].first.first=circles[i][0];
			circle[i].first.second=circles[i][1];
		}
		for (int i = 0; i < n; ++i) {
			if (circle[i].first.first <= circle[i].second) {
				g[n].push_back(i);
				g[i].push_back(n);
			}
			if (X - circle[i].first.first <= circle[i].second) {
				g[n+2].push_back(i);
				g[i].push_back(n+2);
			}
			if (circle[i].first.second <= circle[i].second) {
				g[n+1].push_back(i);
				g[i].push_back(n+1);
			}
			if (Y - circle[i].first.second <= circle[i].second) {
				g[n+3].push_back(i);
				g[i].push_back(n+3);
			}
			for (int j=i + 1; j < n; ++j) {
				double d=sqrt(pow(circle[i].first.first - circle[j].first.first, 2) +pow(circle[i].first.second - circle[j].first.second, 2));
				int r=circle[i].second + circle[j].second;
				if (r>=d) {
					g[i].push_back(j);
					g[j].push_back(i);
				}
			}
		}
		f = max(f,bfs(g,n,n+1,n+2,n+4));
		f = max(f,bfs(g,n+3,n+2,n+1,n+4));
		if (f)return false;
		return true;
	}
};
