typedef long long int ll;

class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {

        auto sq = [](ll v)->ll{
            return v*v;
        };

        auto withinCircle = [&sq]( ll x, ll y, ll xc, ll yc, ll r)->bool {
            return sq(x-xc) + sq(y-yc) <= sq(r);
        };

        auto intersectCircle = [&sq]( ll xc1, ll yc1, ll r1, ll xc2, ll yc2, ll r2)->bool {
            return sq(xc1-xc2)+sq(yc1-yc2)<=sq(r1+r2);
        };

        for( auto & circle: circles) {
            ll xc = circle[0];
            ll yc = circle[1];
            ll r = circle[2];
            if(withinCircle( 0, 0, xc, yc, r) || withinCircle( X, Y, xc, yc, r)) {
                return false;
            }
        }


        ll n = circles.size();

        vector< vector<ll> > adj( n, vector<ll>());
        vector<bool> vst( n, false);

        for( ll i=0; i<n; i++) {
            ll xc1 = circles[i][0];
            ll yc1 = circles[i][1];
            ll r1 = circles[i][2];
            for( ll j=i+1; j<n; j++) {
                ll xc2 = circles[j][0];
                ll yc2 = circles[j][1];
                ll r2 = circles[j][2];
                if(intersectCircle( xc1, yc1, r1, xc2, yc2, r2)) {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }

        for( ll i=0; i<n; i++) {
            if(vst[i]) {
                continue;
            }
            vector<ll> grp;
            queue<ll> q;
            q.push(i);
            vst[i] = true;
            while(!q.empty()) {
                ll u = q.front();
                q.pop();
                grp.push_back(u);
                for( ll v: adj[u]) {
                    if(!vst[v]) {
                        vst[v] = true;
                        q.push(v);
                    }
                }
            }
            bool sx = false;
            bool ex = false;
            bool sy = false;
            bool ey = false;
            for( ll id: grp) {
                ll xc = circles[id][0];
                ll yc = circles[id][1];
                ll r = circles[id][2];
                sx = sx || (abs(yc)<=r);
                sy = sy || (abs(xc)<=r);
                ex = ex || (abs(Y-yc)<=r);
                ey = ey || (abs(X-xc)<=r);
            }
            if(
                (sx && sy) ||
                (ex && ey) || 
                (sx && ex) || 
                (sy && ey)
            ) {
                return false;
            }
        }

        return true;
    }
};
